namespace Smile\ElasticsuiteAbCampaign\Controller\Adminhtml\Campaign;

use Smile\ElasticsuiteAbCampaign\Controller\Adminhtml\AbstractCampaign as CampaignController;

/**
 * Create Campaign controller
 *
 * @category Smile
 * @package  Smile\ElasticsuiteAbCampaign
 * @author   Pierre Le Maguer <pierre.lemaguer@smile.fr>
 */
class Create extends CampaignController
{
    /**
     * {@inheritdoc}
     */
    public function execute()
    {
        /** @var \Magento\Backend\Model\View\Result\Page $resultPage */
        $resultPage = $this->createPage();

        $resultPage->setActiveMenu('Smile_ElasticsuiteAbCampaign::manage_campaign');
        $resultPage->getConfig()->getTitle()->prepend(__('New Campaign'));

        return $resultPage;
    }
}
